<?php include("../../resource/hpe/init_ps.php");$header_title="Student";$header_desc="Admission | New student confirmation";$form_time=array("start"=>1621843200,"end"=>1621935000 );$_SESSION['user_data']["form_intime"]=(time()>=$form_time["start"]&&time()<$form_time["end"]);if(isset($_SESSION['user_auth'])){include("../../resource/appwork/db_connect.php");$cnfdata=$db->query("SELECT cfm,cgroup,time,ip FROM chdata WHERE stdcode='".$_SESSION['user_id']."'");if($cnfdata->num_rows==1){while($rs=$cnfdata->fetch_assoc()){$_SESSION['user_data']["adm"]=array("time"=>$rs['time'],"ip"=>$rs['ip'],"step"=>2,"cgroup"=>$rs['cgroup'],"cfm"=>$rs['cfm']);}}else $_SESSION['user_data']["adm"]=array("step"=>($_SESSION['user_data']["form_intime"]?1:0));} ?><!doctypehtml><html xmlns="http://www.w3.org/1999/xhtml"><head><?php include("../../resource/hpe/heading.php");include("../../resource/hpe/init_ss.php"); ?><style type="text/css">main div.container{position:relative;top:50px;left:50%;transform:translateX(-50%);max-width:95%}@media (min-width:1200px){main div.container{width:1170px}}@media (min-width:992px){main div.container{width:970px}}@media (min-width:768px){main div.container{width:750px}}main div.container div.form-action{margin-top:50px}main div.container div.form-action form{padding:0 12.5px;background-color:var(--clr-bs-light);border-radius:5px;border:1px solid var(--clr-bs-gray)}main div.container div.form-action form>*{margin:25px 0}main div.container div.form-action div.message{line-height:30px}</style><script type="text/javascript">function open_unconfirmed_form(){document.querySelector("main div.container div.form-action").innerHTML='<center><br>ยืนยันสายการเรียน<br><br><button class="blue" onClick="app.sys.auth.check()">ดำเนินการ</button></center>'}function open_confirmation_form(){var e=new XMLHttpRequest;e.open("GET","/reg/student/admission/confirm.php",!0),e.responseType="text",e.onload=function(){document.querySelector("main div.container div.form-action").innerHTML=this.responseText,$(window).trigger("resize")},e.send(),"undefined"==typeof cnf&&$.ajax({url:"/reg/resource/js/extend/std-adm-subform.js"})}function open_complete_form(eti=0){if (eti==0) document.querySelector("main div.container div.form-action").innerHTML = '<center><div class="message gray">You are unauthorized</div></center>';else{var estr = ((!(typeof eti.time === "undefined") || !(typeof eti.ip === "undefined"))?"<br>":"")+(typeof eti.time === "undefined"?"":"ณ วันเวลาที่ "+eti.time+" ")+(typeof eti.ip === "undefined"?"":"ผ่าน IP "+eti.ip);if (!(typeof eti.cgr === "undefined")) estr += '<br>โดยได้'+(eti.cfm=="Y"?'ยืนยันสิทธิ์กลุ่มการเรียน '+eti.cgr:"สละสิทธิ์การเข้าศึกษาต่อที่โรงเรียนบดินทรเดชา (สิงห์ สิงหเสนี)");document.querySelector("main div.container div.form-action").innerHTML = '<center><div class="message '+(eti.cfm=="Y"?"green":"red")+'"><?php if(isset($_SESSION['user_name']))echo $_SESSION['user_name']; ?>รหัสประจำตัวนักเรียน / เลขประจำตัวผู้สอบ<?php if(isset($_SESSION['user_id']))echo $_SESSION['user_id']; ?>ได้ดำเนินการเรียบร้อยแล้ว'+estr+'</div></center>';}}function open_not_time_form(){document.querySelector("main div.container div.form-action").innerHTML='<center><div class="message blue">ขณะนี้อยู่นอกเวลาใช้งานระบบ</div></center>'}function open_error_form(){document.querySelector("main div.container div.form-action").innerHTML='<center><div class="message yellow"><pre>Internal Server 500</pre></div></center>'}$(document).ready(function() {<?php if(isset($_SESSION['user_auth'])){include("../../resource/appwork/appfunc.php");switch($_SESSION['user_data']["adm"]["step"]){case 0:echo 'open_not_time_form();';break;case 1:echo 'open_confirmation_form();';break;case 2:echo 'open_complete_form({time: "'.$_SESSION['user_data']["adm"]["time"].'", ip: "'.$_SESSION['user_data']["adm"]["ip"].'", cgr: "'.code2group($_SESSION['user_data']["adm"]["cgroup"]).'", "cfm": "'.$_SESSION['user_data']["adm"]["cfm"].'"});';break;case 3:echo 'open_error_form();';break;default:echo 'open_unconfirmed_form();';break;}}if(isset($_GET['status']))echo "disp_status(".$_GET['status'].(isset($_GET['reason'])?", '".$_GET['reason']."'":"").");"; ?>setTimeout(function() { $(window).trigger("resize"); }, 500);});function refresh_statement(){document.querySelector("main div.container div.form-action").innerHTML='<center><div class="info"><br>ตรวจสอบสถานะการยืนยัน<br><br><button class="blue" onClick="location.reload()">โหลด</button></div></center>'}function disp_status(e,a=""){var t,n;switch(history.replaceState(null,null,location.pathname),e){case 0:t=0,n=a+"Your data has been record.";break;case 1:t=3,n="Unable to upload evidence. Please try again.";break;case 2:t=3,n="Unable to connect to database. Please try again.";break;case 3:t=1,n="You've already made a record at "+a}app.ui.notify(1,[t,n])}</script></head><body><?php include("../../resource/hpe/header.php"); ?><main shrink="<?php echo($_COOKIE['sui_open-nt'])??"false"; ?>"><div class="container"><div class="bc"><h2>ประกาศแจ้งนักเรียนชั้นมัธยมศึกษาปีที่ 4 ปีการศึกษา 2564</h2><p>  ขอความร่วมมือนักเรียนชั้นมัธยมศึกษาปีที่ 3 เดิม (ที่รายงานตัวเข้าศึกษาต่อชั้นมัธยมศึกษาปีที่ 4 ในวันที่ 24 มีนาคม 2564) และนักเรียนที่ได้รับการคัดเลือกเข้าศึกษาชั้นมัธยมศึกษาปีที่ 4 ตามประกาศโรงเรียนบดินทรเดชา (สิงห์ สิงหเสนี) ลงวันที่ 24 พฤษภาคม 2564 ยืนยันการเข้าศึกษา ภายในวันที่ 25 พฤษภาคม 2564 เวลา 16.30 น.</p></div><div class="form-action"></div></div></main><?php include("../../resource/hpe/material.php"); ?><footer><?php include("../../resource/hpe/footer.php"); ?></footer></body></html>
