<?php session_start();function ldap_authen($server,$base_dn,$useraccount,$password){$ldapserver=ldap_connect($server);ldap_set_option($ldapserver,LDAP_OPT_PROTOCOL_VERSION,3);ldap_set_option($ldapserver,LDAP_OPT_TIMELIMIT,6);ldap_set_option($ldapserver,LDAP_OPT_REFERRALS,0);$useraccountad="BODIN\\".$useraccount;$bind=@ldap_bind($ldapserver,$useraccountad,$password);$authen="true";if(!$bind)$authen="false";$filter="samaccountname=".$useraccount;$result=@ldap_search($ldapserver,$base_dn,$filter);$info=@ldap_get_entries($ldapserver,$result);if(!$result)$authen="false";if($info["count"]==0)$authen="false";if($info["count"]>1)$authen="false";if(isset($info[0]["distinguishedName"]))$user_dn=$info[0]["distinguishedName"];else $user_dn="";$bind=@ldap_bind($ldapserver,$useraccountad,$password);if(!$bind)$authen="false";ldap_close($ldapserver);return $authen;}if(isset($_POST['zone']))$zone=$_POST['zone'];else $zone=0;if(isset($_POST['username'])&&isset($_POST['password'])){include("../resource/appwork/db_connect.php");$_POST['username']=str_replace("*","",strtolower(trim($_POST['username'])));if($_POST['username']<>""&&$_POST['password']<>""){if(strlen($_POST['username'])<6){$authenzone[0][0]="ou=bd52,ou=student,dc=bodin,dc=ac,dc=th";$authenzone[0][1]="ldap01.bodin.ac.th";$authenzone[0][2]="ldap02.bodin.ac.th";$authen="false";$i=1;while($i<count($authenzone[$zone])){$authen=ldap_authen($authenzone[$zone][$i],$authenzone[$zone][0],$_POST['username'],$_POST['password']);if($authen=="true"){$i=count($authenzone[$zone]);$zonename=substr($authenzone[$zone][0],3,7);}else $i++;}$search_sql="";}else{$_POST['username']=strval($db->real_escape_string($_POST['username']));$_POST['password']=strval($db->real_escape_string($_POST['password']));$search_sql=" AND natid='".$_POST['password']."'";$authen="true";}$newstdlogin=$db->query("SELECT * FROM stddata WHERE stdcode='".$_POST['username']."'$search_sql");if($newstdlogin->num_rows==1){while($ers=$newstdlogin->fetch_assoc())$stddata=$ers;}if($authen=="true"&&isset($stddata)){echo '{"success": true}';$_SESSION['user_auth']=1;$_SESSION['user_name']=$stddata['name'];$_SESSION['user_id']=$stddata['stdcode'];$_SESSION['user_data']=array("natid"=>$stddata['natid']);$secondary_id=$db->query("SELECT stdcode FROM stddata WHERE natid='".$_SESSION['user_data']["natid"]."' AND stdcode<>'".$_SESSION['user_id']."'");if($secondary_id->num_rows==1){while($gsi=$secondary_id->fetch_assoc())$_SESSION['user_data']["code2"]=$gsi['stdcode'];}}else{echo '{"success": false, "reason": '.($authen=="true"?'[1, "There isn\'t any record for your account yet."]':'[3,"Incorrect username or password."]').'}';}}$db->close();}else{header("Location: /reg/student/");} ?>